import React, { useMemo } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Fix for default marker icon
import icon from 'leaflet/dist/images/marker-icon.png';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';

const destinations = [
    { id: 1, name: "Cox's Bazar", coords: [21.4272, 92.0058], description: "Longest natural sandy beach in the world." },
    { id: 2, name: "Bandarban", coords: [22.1953, 92.2184], description: "The roof of Bangladesh with majestic hills." },
    { id: 3, name: "Rangamati", coords: [22.6574, 92.1733], description: "The lake city with serene boat rides." },
    { id: 4, name: "Chattogram City", coords: [22.3569, 91.7832], description: "Gateway to the hills and a historic port city." },
    { id: 5, name: "Saint Martin's Island", coords: [20.6277, 92.3233], description: "The only coral island in Bangladesh." },
    { id: 6, name: "Khagrachari", coords: [23.1192, 91.9841], description: "Land of waterfalls and mysterious caves." }
];

const InteractiveMap = () => {
    const customIcon = useMemo(() => new L.Icon({
        iconUrl: icon,
        shadowUrl: iconShadow,
        iconSize: [25, 41],
        iconAnchor: [12, 41],
        popupAnchor: [1, -34],
    }), []);

    return (
        <section id="map" className="map-section">
            <div className="container">
                <div className="section-header">
                    <span className="section-badge section-badge-accent">Interactive Map</span>
                    <h2>Explore the Region</h2>
                    <p>Locate your next adventure on the map and see proximity between destinations</p>
                </div>

                <div className="map-container-wrapper">
                    <MapContainer
                        center={[21.8, 92.1]}
                        zoom={8}
                        scrollWheelZoom={false}
                        className="leaflet-container-custom"
                    >
                        <TileLayer
                            attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        />
                        {destinations.map((dest) => (
                            <Marker
                                key={dest.id}
                                position={dest.coords}
                                icon={customIcon}
                            >
                                <Popup>
                                    <div className="map-popup">
                                        <h3 style={{ margin: '0 0 5px 0' }}>{dest.name}</h3>
                                        <p style={{ margin: '0 0 10px 0' }}>{dest.description}</p>
                                        <a href={`#destinations`} style={{ fontWeight: 600, color: 'var(--primary)', textDecoration: 'none' }}>View Details</a>
                                    </div>
                                </Popup>
                            </Marker>
                        ))}
                    </MapContainer>
                </div>
            </div>
        </section>
    );
};

export default InteractiveMap;
